import { FunctionComponent, useState, useRef } from "react";
import styled from "styled-components";
import { theme } from "../../styles/theme/theme";

import TextArea from "./textbox";
import InputsContainer from "./inputs-container";
import DateTimeInput from "./input";
import CreateButtons from "./button";

import { handleSubmitImageChange } from "./services/changeServices";

const Container = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  z-index: 500;

  width: 100vw;
  height: 150px;
  padding: ${theme.spacing.space.large} ${theme.spacing.space.xxxlage};

  display: flex;
  align-items: center;
  justify-content: space-between;

  background-color: ${theme.colors.ui.main};
  box-shadow: 0 -20px 30px rgba(0, 0, 0, 0.2);
`;

const FileInput = styled.input`
  display: none;
  opacity: 100%;
`;

const CreateWindow: FunctionComponent = () => {
  const imageButton = useRef() as React.MutableRefObject<HTMLInputElement>;
  const [file, setFile] = useState<File | null>();
  const [tweet, setTweet] = useState({
    message: "",
    date: "",
    time: "",
  });

  return (
    <Container>
      <TextArea />
      <InputsContainer>
        <DateTimeInput type={"date"} />
        <DateTimeInput type={"time"} />
        <CreateButtons text="add image" />
        <CreateButtons text="submit" />
        <FileInput id="file-select" type="file" accept="image/*" onChange={() => {}} ref={imageButton} />
      </InputsContainer>
    </Container>
  );
};

export default CreateWindow;
