import styled, { css } from "styled-components";
import { theme } from "../../styles/theme/theme";

export const ButtonHover = css`
  &:hover {
    transform: scale(102%);
    cursor: pointer;
    box-shadow: 0 5px 14px rgba(0, 0, 0, 0.3);
  }
`;
