import { useContext, useEffect, useState } from "react";
import "./styles/global.css";
import LoginBox from "./components/login-box";
import MainView from "./views/main/main-view";
import * as Cookies from "js-cookie";
import CreateWindow from "./components/create-window";
import ModalContext from "./context/modal/ModalContext";
import JobsContext from "./context/jobs/JobsContext";
import Modal from "./components/modal";
import AuthContext from "./context/auth/AuthContext";
import { Routes, Route, BrowserRouter as Router } from "react-router-dom";

function App() {
  const [showLogin, setShowLogin] = useState(true);
  const jobsContext = useContext(JobsContext);
  const { jobsState } = jobsContext;
  const modalContext = useContext(ModalContext);
  const { modalState } = modalContext;
  const authContext = useContext(AuthContext);
  const { getUserData } = authContext;

  useEffect(() => {
    if (Cookies.get("token")) {
      getUserData();
    }
  }, []);

  return (
    <Router>
      <Routes>
        {modalState.show ? <Modal /> : null}
        {showLogin ? <LoginBox setShowLogin={setShowLogin} /> : null}
        <Route path="/dashboard">
          <MainView />
        </Route>
        {jobsState.showCreateWindow === true ? <CreateWindow /> : null}
      </Routes>
    </Router>
  );
}

export default App;
