    dispatch({ type: SHOW_WINDOW });
  };

  const closeCreateWindow = () => {
    console.log("Close create window");
    dispatch({ type: CLOSE_WINDOW });
  };

  return (
    <JobsContext.Provider
      value={{
        jobsState,
        getAllJobs,
        deletePostByID,
        showCreateWindow,
        closeCreateWindow,
      }}
    >
      {props.children}
    </JobsContext.Provider>
  );
};

export default JobState;
