import { FunctionComponent } from "react";
import styled from "styled-components";
import { theme } from "../../styles/theme/theme";

import { NormalText } from "../resuable-styled/text";
import { IRequestJobObject } from "../../types/types";

import DeleteButton from "./delete-button";

const Container = styled.div`
  width: 100%;
  height: 80px;
  padding: 0 ${theme.spacing.space.xlarge};

  border-radius: 20px;
  border: 1px solid ${theme.colors.border.main};

  display: grid;
  grid-template-columns: 4fr 2fr 1fr;

  grid-column-gap: ${theme.spacing.space.medium};
  margin-bottom: ${theme.spacing.space.medium};

  transition: all 0.2s ease-in-out;

  &:hover {
    transform: scale(102%);
    cursor: pointer;
    box-shadow: 0 5px 14px rgba(0, 0, 0, 0.3);
  }
`;

const DateTimeArea = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;

  width: 100%;
`;

const Message = styled(NormalText)`
  font-size: ${theme.fonts.fontsizes.medium};
  align-self: center;
`;

// Interface for this job object?
