import { FunctionComponent, useContext, useEffect, useState } from "react";
import styled from "styled-components";
import { theme } from "../../styles/theme/theme";
import { Title } from "../resuable-styled/text";
import LoginButton from "./button";
import LoginProfileImage from "./profile-image";
import * as Cookies from "js-cookie";
import AuthContext from "../../context/auth/AuthContext";
import { useSpring, animated } from "react-spring";

const Container = styled(animated.div)`
  width: 400px;
  height: 500px;
  z-index: 10;
  background-color: ${theme.colors.ui.main};
  border-radius: 15px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
`;

const LoginTitle = styled(Title)`
  margin-bottom: ${theme.spacing.space.xxlarge};
`;

const LoginBox: FunctionComponent = () => {
  const [loggedin, setLoggedIn] = useState(false);
  const authContext = useContext(AuthContext);
  const { authState, getUserData } = authContext;

  // const Container = styled(animated.div)``
  // <Container style={animationProps}>

  const windowHideAnimation = useSpring({
    from: { opacity: 1 },
    to: { opacity: 0 },
    delay: 1000,
  });

  useEffect(() => {
    if (Cookies.get("token")) {
      getUserData();
    }

    if (authState.user) {
      setLoggedIn(true);
    }
  }, []);

  return (
    <Container style={loggedin ? windowHideAnimation : null}>
      {!loggedin ? <LoginTitle>Welcome to Tweety</LoginTitle> : null}
      {loggedin ? <LoginTitle>Welcome {authState.user.displayName}</LoginTitle> : null}
      <LoginProfileImage />
      {!loggedin ? <LoginButton /> : null}
    </Container>
  );
};

export default LoginBox;
