import React, { FunctionComponent, useState, useRef, useContext } from "react";
import axios from "axios";
import * as Cookies from "js-cookie";
import styled from "styled-components";
import { theme } from "../../styles/theme/theme";

import TextArea from "./textbox";
import InputsContainer from "./inputs-container";
import DateTimeInput from "./input";
import CreateButtons from "./button";

import { ERequestOutcomes } from "../../types/errors";
import { uploadImageFile } from "./services/dbServices";

import { checkDateInputs, checkWordCount } from "../../utils/check-inputs";

const Container = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  z-index: 500;

  width: 100vw;
  height: 150px;
  padding: ${theme.spacing.space.large} ${theme.spacing.space.xxxlage};

  display: flex;
  align-items: center;
  justify-content: space-between;

  background-color: ${theme.colors.ui.main};
  box-shadow: 0 -20px 30px rgba(0, 0, 0, 0.2);
`;

const FileInput = styled.input`
  display: none;
  opacity: 100%;
`;

const CreateWindow: FunctionComponent = () => {
  const imageInputElement = useRef() as React.MutableRefObject<HTMLInputElement>;
  const [file, setFile] = useState<File | null>();
  const [tweet, setTweet] = useState({
    message: "",
    date: "",
    time: "",
  });

  const messageChangeHandler = (e: React.ChangeEvent) => {
    const target = e.target as HTMLInputElement;
    setTweet({ ...tweet, message: target.value });
  };

  const dateChangeHandler = (e: React.MouseEvent) => {
    const target = e.target as HTMLInputElement;
    setTweet({ ...tweet, date: target.value });
  };

  const timeChangeHandler = (e: React.MouseEvent) => {
    const target = e.target as HTMLInputElement;
    setTweet({ ...tweet, time: target.value });
  };

  const imageOnClickHandler = () => {
    imageInputElement.current.click();
  };

  const imageOnChangeHandler = (e: React.ChangeEvent) => {
    const target = e.target as HTMLInputElement;
    setFile(target.files![0]);
  };

  const uploadImage = async () => {
    if (!file) return ERequestOutcomes.isEmpty;
    const uploadImageReq = await uploadImageFile(file);
    if (uploadImageReq.hasError) return ERequestOutcomes.hasError;
    return uploadImageReq;
  };

  //! Need to add modal and resposnes from modal..
  const handleSubmitClick = async () => {
    if (!checkWordCount(tweet.message.length)) return console.log("MODAL");
    if (!checkDateInputs(tweet.date, tweet.time)) return console.log("MODAL");

    const uploadImageAttempt = await uploadImage();
    if (uploadImageAttempt === ERequestOutcomes.hasError) return console.log("MODAL");

    try {
      const post = await axios.request({
        method: "POST",
        withCredentials: true,
        url: `http://localhost:3333/api/post/message`,
        data: {
          date: tweet.date,
          time: tweet.time,
          message: tweet.message,
          imageURL: uploadImageAttempt !== ERequestOutcomes.isEmpty ? uploadImageAttempt.url : null,
          imageName: uploadImageAttempt !== ERequestOutcomes.isEmpty ? uploadImageAttempt.name : null,
        },
        headers: {
          authorization: `${Cookies.get("token")}`,
        },
      });

      console.log("SUCCESS MODAL");
    } catch (error) {
      console.log("ERROR MODAL");
      return;
    }
  };

  return (
    <Container>
      <TextArea onChange={messageChangeHandler} />
      <InputsContainer>
        <DateTimeInput type={"date"} onChange={dateChangeHandler} />
        <DateTimeInput type={"time"} onChange={timeChangeHandler} />
        <CreateButtons text="add image" onClick={imageOnClickHandler} />
        <CreateButtons text={"submit"} onClick={handleSubmitClick} />
        <FileInput
          id="file-select"
          type="file"
          accept="image/*"
          ref={imageInputElement}
          onChange={imageOnChangeHandler}
        />
      </InputsContainer>
    </Container>
  );
};

export default CreateWindow;
