import { FunctionComponent, useContext } from "react";
import styled from "styled-components";
import { theme } from "../../styles/theme/theme";
import { Title } from "../resuable-styled/text";
import LoginButton from "./button";
import { NormalText } from "../resuable-styled/text";
import AuthContext from "../../context/auth/AuthContext";

export const WindowContainer = styled.div`
  width: 100vw;
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;

  background-color: ${theme.colors.ui.main};

  overflow: hidden;
`;

export const Window = styled.div`
  width: calc(100vw - ${theme.spacing.space.xxxlage});
  height: calc(100vh - ${theme.spacing.space.xxxlage});

  background-color: ${theme.colors.ui.main};
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.35);
  border-radius: 20px;

  padding: ${theme.spacing.space.xlarge} ${theme.spacing.space.xxlarge};

  display: flex;
  flex-direction: column;

  @media (max-width: 750px) {
    width: 100vw;
    height: 100vh;
    border-radius: 0;
    padding: ${theme.spacing.space.medium} ${theme.spacing.space.large};
  }
`;

const LoginArea = styled.button`
  width: 400px;
  height: 300px;
  z-index: 10;
  background-color: ${theme.colors.ui.main};
  border-radius: 15px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
`;

const LoginTitle = styled(Title)`
  margin-bottom: ${theme.spacing.space.xlarge};
`;

const SubTitle = styled(NormalText)``;

const Image = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  margin-bottom: ${theme.spacing.space.xlarge};
`;

interface IProps {
  setShowLogin: any;
}

const LoginBox: FunctionComponent<IProps> = ({ setShowLogin }) => {
  const authContext = useContext(AuthContext);
  const { authState } = authContext;

  return (
    <WindowContainer>
      <Window>
        <LoginArea>
          {console.log(authState.user)}
          {!authState.user ? (
            <>
              <LoginTitle>Welcome to Tweety</LoginTitle>
              <SubTitle>The simple tweet scheduler.</SubTitle>
              <LoginButton />
            </>
          ) : null}

          {authState.user ? (
            <>
              <LoginTitle>Welcome {authState.user.displayName}</LoginTitle>
              <Image src={authState.user.profileImage} alt="userimage"></Image>
              <SubTitle>Please wait, loading your data.</SubTitle>
            </>
          ) : null}
        </LoginArea>
      </Window>
    </WindowContainer>
  );
};

export default LoginBox;
