import React, { FunctionComponent, useContext, useState } from "react";
import styled from "styled-components";
import { theme } from "../../styles/theme/theme";
import { ButtonHover } from "../mixins/mixins";

import JobsContext from "../../context/jobs/JobsContext";

const Container = styled.button`
  color: ${theme.colors.text.white};
  background-color: ${theme.colors.ui.blue};

  width: 100px;
  height: 40px;

  border-radius: 15px;

  outline: none;
  border: none;

  font-size: ${theme.fonts.fontsizes.medium};

  ${ButtonHover}
`;

const CreateNewButton: FunctionComponent = () => {
  const jobsContext = useContext(JobsContext);
  const { showCreateWindow, closeCreateWindow } = jobsContext;

  const [windowOpen, setWindowOpen] = useState(false);

  const onClickHandler = (e: React.MouseEvent) => {
    showCreateWindow();
    if (windowOpen) {
      setWindowOpen(false);
      closeCreateWindow();
    }

    if (!windowOpen) {
      setWindowOpen(true);
      showCreateWindow();
    }
  };

  return <Container onClick={onClickHandler}>{windowOpen ? "Close" : "New"}</Container>;
};

export default CreateNewButton;
