import { FunctionComponent } from "react";
import styled from "styled-components";
import { theme } from "../../styles/theme/theme";

import { NormalText } from "../resuable-styled/text";
import { IRequestJobObject } from "../../types/types";

import DeleteButton from "./delete-button";
import { HoverOutlineClick } from "../../mixins/mixins";

import { formateTimeToString, formatDateToString } from "./utils/formate-date";

const Container = styled.div`
  width: 100%;
  height: 80px;
  padding: 0 ${theme.spacing.space.xlarge};

  border-radius: 20px;
  border: 1px solid ${theme.colors.border.main};

  display: grid;
  grid-template-columns: 4fr 2fr min-content;

  grid-column-gap: ${theme.spacing.space.medium};
  margin-bottom: ${theme.spacing.space.medium};

  transition: all 0.2s ease-in-out;

  ${HoverOutlineClick}
`;

const DateTimeArea = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;

  width: 100%;
`;

const Message = styled(NormalText)`
  font-size: ${theme.fonts.fontsizes.medium};
  align-self: center;
`;

// Interface for this job object?
interface IProps {
  jobData: IRequestJobObject;
}

const ScheduleItem: FunctionComponent<IProps> = ({ jobData }) => {
  return (
    <Container>
      <Message>{jobData.message}</Message>
      <DateTimeArea>
        <NormalText>{jobData.status}</NormalText>
        <NormalText>{formatDateToString(jobData.date)}</NormalText>
        <NormalText>{formateTimeToString(jobData.date)}</NormalText>
      </DateTimeArea>

      <DeleteButton id={jobData.job_id} />
    </Container>
  );
};

export default ScheduleItem;
