import { FunctionComponent } from "react";
import styled from "styled-components";
import { theme } from "../../styles/theme/theme";

const Container = styled.div`
  width: 60%;
  height: 100%;
`;

const TweetBox = styled.textarea`
  position: relative;
  width: 100%;
  height: 100%;

  outline: none;
  border: solid 1px ${theme.colors.border.main};
  border-radius: 15px;
  padding: ${theme.spacing.space.medium};

  color: ${theme.colors.text.white};
  background-color: ${theme.colors.ui.lightMain};
  resize: none;
`;

const WordCount = styled.p`
  position: absolute;

  right: ${theme.spacing.space.medium};
  bottom: ${theme.spacing.space.medium};
  font-size: ${theme.fonts.fontsizes.small};
  color: ${theme.colors.text.grey};
`;

interface IProps {
  onChange?: (e: any) => void;
  id: string;
  value: string;
}

const TextArea: FunctionComponent<IProps> = ({ onChange, id, value }) => {
  return (
    <Container>
      <TweetBox id={id} value={value} placeholder="Type your tweet, make it a good one!" onChange={onChange}></TweetBox>
    </Container>
  );
};

export default TextArea;
