import { FunctionComponent, ReactNode } from "react";

import styled from "styled-components";
import { theme } from "../../styles/theme/theme";
import { ButtonHover } from "../../mixins/mixins";

const Container = styled.button`
  color: ${theme.colors.text.white};
  background-color: ${theme.colors.ui.blue};

  width: max-content;
  height: max-content;
  padding: ${theme.spacing.space.medium} ${theme.spacing.space.small};

  border-radius: 15px;

  outline: none;
  border: none;

  ${ButtonHover}
`;

const LoginButton: FunctionComponent = () => {
  const onClickHandler = (e: React.MouseEvent) => {
    window.location.replace("http://localhost:3333/api/auth/twitter");
  };

  return <Container onClick={onClickHandler}>Login to twitter!</Container>;
};

export default LoginButton;
