import { FunctionComponent, useContext } from "react";
import styled, { css } from "styled-components";
import { theme } from "../../styles/theme/theme";
import { ButtonHover } from "../../mixins/mixins";
import JobsContext from "../../context/jobs/JobsContext";

const Container = styled.button`
  width: 40px;
  height: 40px;

  background-color: ${theme.colors.ui.blue};
  color: ${theme.colors.text.white};
  font-size: ${theme.fonts.fontsizes.small};
  border-radius: 30%;

  display: flex;
  justify-content: center;
  align-items: center;

  justify-self: end;
  align-self: center;

  transition: all 0.2s;

  outline: none;
  border: none;

  ${ButtonHover}
`;

interface IProps {
  id: string;
}

const DeleteButton: FunctionComponent<IProps> = ({ id }) => {
  const jobsContext = useContext(JobsContext);
  const { deletePostByID } = jobsContext;

  return <Container onClick={() => deletePostByID(id)}>X</Container>;
};

export default DeleteButton;
